import 'package:cached_network_image/cached_network_image.dart';
import 'package:create_product/domain/entities/update_product/function_update_product.dart';
import 'package:create_product/ui/colors.dart';
import 'package:flutter/material.dart';
import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:flutter/services.dart';

class UpdatePage extends StatefulWidget {
  @override
  _UpdatePageState createState() => _UpdatePageState();
}

class _UpdatePageState extends State<UpdatePage> {
  final TextEditingController _nameController = TextEditingController();
  final TextEditingController _priceController = TextEditingController();
  final TextEditingController _descriptionController = TextEditingController();
  // final TextEditingController _imgController = TextEditingController();

  @override
  void initState() {
    super.initState();
    _fetchProductDetails();
  }

  String? name;
  int? price;
  String? img;
  String? des;

  bool _isDescriptionEnabled = true;

  void setStateImage() {
    setState(() {});
  }

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    final args =
        ModalRoute.of(context)?.settings.arguments as Map<String, dynamic>;
    name = args['name'] ?? 'Error al obtener el nombre';
    price = args['price'] ?? 0;
    img = args['img'] ?? url;
    des = args['description'] ?? 'Error al obtener la descripci√≥n';
  }

  Future<void> _fetchProductDetails() async {
    DocumentSnapshot snapshot =
        await FirebaseFirestore.instance.collection('products').doc().get();

    if (snapshot.exists) {
      Map<String, dynamic> data = snapshot.data() as Map<String, dynamic>;
      _nameController.text = data['name'];
      _priceController.text = data['price'].toString();
    }
  }

  Future<void> _updateProduct() async {
    String name = _nameController.text;
    double price = double.parse(_priceController.text);

    await FirebaseFirestore.instance.collection('products').doc().update({
      'name': name,
      'price': price,
    });

    // Show a success message or navigate to another screen
    // after the update is complete.
  }

  final String url =
      'data:image/jpeg;base64,';
  final String urlAddImage =
      'https://firebasestorage.googleapis.com/v0/b/foodie-960f5.appspot.com/o/uploads%2FaddImagen(1).jpg?alt=media&token=c72ae92c-6223-42c4-bd25-4c28ee170816';
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Actualizar producto $name'),
      ),
      body: SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.all(8.0),
          child: Column(
            children: [
              const Text(
                'Producto anterior',
                style: TextStyle(fontSize: 20),
              ),
              Padding(
                padding: const EdgeInsets.all(8.0),
                child: Card(
                  color: Colors.white,
                  child: Row(
                    children: [
                      CachedNetworkImage(
                        imageUrl: img ?? url,
                        placeholder: (context, url) =>
                            const CircularProgressIndicator(),
                        errorWidget: (context, url, error) =>
                            const Icon(Icons.error),
                        width: 150,
                      ),
                      Expanded(
                        child: ListTile(
                          title: Text(
                            name != null
                                ? name![0].toUpperCase() + name!.substring(1)
                                : 'Error al cargar el nombre',
                            style: const TextStyle(
                                fontSize: 20, fontWeight: FontWeight.bold),
                          ),
                          subtitle: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(
                                des ?? 'Error al cargar la descripci√≥n',
                                style:
                                    const TextStyle(fontFamily: 'sans-serif'),
                              ),
                              Text(
                                "\$${price ?? '00.00'} MXN",
                                style: const TextStyle(
                                  fontSize: 20,
                                  color: amarillo,
                                  fontWeight: FontWeight.bold,
                                ),
                              ),
                            ],
                          ),
                          trailing: const Row(
                            mainAxisSize: MainAxisSize.min,
                            children: [
                              IconButton(
                                onPressed: null,
                                icon:
                                    Icon(Icons.edit, color: amarillo, size: 50),
                                tooltip: 'Estos botones no hacen nada',
                              ),
                              IconButton(
                                onPressed: null,
                                icon: Icon(Icons.delete,
                                    color: amarillo, size: 50),
                                tooltip: 'Estos botones no hacen nada',
                              ),
                            ],
                          ),
                        ),
                      ),
                    ],
                  ),
                ),
              ),
              const Divider(color: Colors.black),
              const Text(
                'Nuevo producto',
                style: TextStyle(fontSize: 20),
              ),
              Padding(
                padding: const EdgeInsets.all(8.0),
                child: Card(
                  color: Colors.white,
                  child: Row(
                    children: [
                      // Image.network(
                      //   FunctionUpdateProduct.imageUrl ?? url,
                      //   key: ValueKey(FunctionUpdateProduct.imageUrl),
                      //   width: 300,
                      //   height: 400,
                      //   fit: BoxFit.cover,
                      // ),
                      InkWell(
                        onTap: () {
                          FunctionUpdateProduct().uploadFile(setStateImage);
                        },
                        child: Column(
                          children: [
                            CachedNetworkImage(
                              imageUrl:
                                  FunctionUpdateProduct.imageUrl ?? urlAddImage,
                              key: ValueKey(FunctionUpdateProduct.imageUrl),
                              width: 150,
                              fit: BoxFit.cover,
                            ),
                          ],
                        ),
                      ),
                      Expanded(
                        child: ListTile(
                          title: SizedBox(
                            child: TextField(
                              controller: _nameController,
                              decoration: const InputDecoration(
                                labelText: 'Nombre del producto',
                              ),
                              style: const TextStyle(
                                  fontSize: 20, fontWeight: FontWeight.bold),
                            ),
                          ),
                          subtitle: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              TextFormField(
                                controller: _descriptionController,
                                decoration: const InputDecoration(
                                  labelText: 'Descripci√≥n',
                                ),
                                keyboardType: TextInputType.multiline,
                                inputFormatters: <TextInputFormatter>[
                                  FilteringTextInputFormatter
                                      .singleLineFormatter
                                ],
                                style:
                                    const TextStyle(fontFamily: 'sans-serif'),
                                enabled: _isDescriptionEnabled,
                              ),
                              Padding(
                                padding: const EdgeInsets.all(8.0),
                                child: ElevatedButton(
                                  onPressed: () {
                                    setState(() {
                                      _isDescriptionEnabled =
                                          !_isDescriptionEnabled;
                                      _descriptionController.text = '';
                                    });
                                  },
                                  style: ElevatedButton.styleFrom(
                                    backgroundColor: amarillo,
                                  ),
                                  child: Text(
                                    _isDescriptionEnabled
                                        ? 'Deshabilitar'
                                        : 'Habilitar',
                                    style: const TextStyle(
                                      fontSize: 20,
                                      color: Colors.white,
                                      fontWeight: FontWeight.bold,
                                    ),
                                  ),
                                ),
                              ),
                              Row(
                                children: [
                                  const Padding(
                                    padding: EdgeInsets.all(8.0),
                                    child: Text(
                                      '\$',
                                      style: TextStyle(
                                        fontSize: 20,
                                        color: amarillo,
                                        fontWeight: FontWeight.bold,
                                      ),
                                    ),
                                  ),
                                  SizedBox(
                                    width: 90,
                                    child: TextField(
                                      controller: _priceController,
                                      decoration: const InputDecoration(
                                        labelText: 'Precio',
                                      ),
                                      style: const TextStyle(
                                        fontSize: 20,
                                        color: amarillo,
                                        fontWeight: FontWeight.bold,
                                      ),
                                      keyboardType: TextInputType.number,
                                      inputFormatters: <TextInputFormatter>[
                                        FilteringTextInputFormatter.digitsOnly
                                      ],
                                    ),
                                  ),
                                  const Text(
                                    'MXN',
                                    style: TextStyle(
                                      fontSize: 20,
                                      color: amarillo,
                                      fontWeight: FontWeight.bold,
                                    ),
                                  ),
                                ],
                              ),
                            ],
                          ),
                          trailing: const Row(
                            mainAxisSize: MainAxisSize.min,
                            children: [
                              IconButton(
                                onPressed: null,
                                icon:
                                    Icon(Icons.edit, color: amarillo, size: 50),
                                tooltip: 'Estos botones no hacen nada',
                              ),
                              IconButton(
                                onPressed: null,
                                icon: Icon(Icons.delete,
                                    color: amarillo, size: 50),
                                tooltip: 'Estos botones no hacen nada',
                              ),
                            ],
                          ),
                        ),
                      ),
                    ],
                  ),
                ),
              ),
              ElevatedButton(
                onPressed: null,
                style: ElevatedButton.styleFrom(
                  backgroundColor: amarillo,
                ),
                child: const Text('Actualizar producto'),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
